import React, { useMemo } from "react";

const StarBackground = () => {
  // Generate stars once using useMemo to avoid re-randomization on re-renders
  const stars = useMemo(
    () =>
      Array.from({ length: 25 }).map((_, i) => ({
        id: i,
        top: `${Math.random() * 100}%`,
        left: `${Math.random() * 100}%`,
        size: `${Math.random() * 4 + 2}px`, // Size between 2px - 6px
        delay: `${Math.random() * 5}s`, // Delay up to 5s for natural twinkling
      })),
    []
  );

  return (
    <div className="fixed inset-0 -z-10 pointer-events-none overflow-hidden">
      {stars.map((star) => (
        <div
          key={star.id}
          className="absolute rounded-full bg-pink-400 animate-[twinkle_3s_infinite_alternate]"
          style={{
            top: star.top,
            left: star.left,
            width: star.size,
            height: star.size,
            animationDelay: star.delay,
          }}
        />
      ))}
    </div>
  );
};

export default StarBackground;
