import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Scale, Search, X, ChefHat } from 'lucide-react';
import { motion } from 'framer-motion';
import "../components/";

const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";;
const weightUnits = ['g', 'kg', 'oz', 'lb'];
const volumeUnits = ['ml', 'liter', 'tsp', 'tbsp', 'cup', 'glass'];
const allUnits = [...weightUnits, ...volumeUnits];

const ConversionForm = () => {
  const [ingredients, setIngredients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIngredient, setSelectedIngredient] = useState('');
  const [amount, setAmount] = useState('1');
  const [fromUnit, setFromUnit] = useState('cup');
  const [toUnit, setToUnit] = useState('g');
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const dropdownRef = useRef(null);

  // Fetch ingredients
  useEffect(() => {
    const fetchIngredients = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/foods`);
        setIngredients(response.data);
      } catch (err) {
        console.error('Error fetching ingredients:', err);
        setError('Failed to load ingredients');
      }
    };
    fetchIngredients();
  }, []);

  // Normalize text by removing special characters and converting to lowercase
  const normalizeText = (text) => 
    text ? text.toLowerCase().replace(/[\s,./-]/g, "") : "";
  

  const filteredIngredients = searchTerm
  ? ingredients
      .filter(item => typeof item === 'string')
      .filter(item => normalizeText(item).includes(normalizeText(searchTerm)))
  : [];




  // Handle click outside dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Conversion handler
  const handleConvert = async (e) => {
    e.preventDefault();
    setError(null);
    setIsLoading(true);

    if (!selectedIngredient || !amount) {
      setError("Please fill all fields");
      setIsLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/convert`, {
        food: selectedIngredient,
        quantity: parseFloat(amount),
        from_unit: fromUnit,
        to_unit: toUnit,
      });

      setResult({
        input: `${amount} ${fromUnit}`,
        output: response.data.output,
        ingredient: selectedIngredient
      });
    } catch (err) {
      setError(err.response?.data?.error || "Conversion failed. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="conversion-container">
      <form onSubmit={handleConvert} className="flex flex-col items-centre">
        {/* Ingredient Search */}
        
        <div className="form-group relative" ref={dropdownRef}>
          <label>🔍 What's cooking today? Start searching an ingredient...</label>
          <div className="search-container">
            <Search className="search-icon" />
            
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => {
                  setSearchTerm(e.target.value);
                  setShowSuggestions(true);
                  setSelectedIngredient(e.target.value);
                }}
                onFocus={() => setShowSuggestions(true)}
                placeholder="Search ingredient..."
              />
              {searchTerm && (
                <X
                  className="clear-icon"
                  onClick={() => {
                    setSearchTerm('');
                    setSelectedIngredient('');
                    setShowSuggestions(false);
                  }}
                />
              )}
              
          </div>

          {/* Suggestions Dropdown */}
          {showSuggestions && filteredIngredients.length > 0 && (
            <div className="suggestions-dropdown">
              {filteredIngredients.map((item, index) => (
                <div
                  key={index}
                  className="suggestion-item"
                  onClick={() => {
                    setSelectedIngredient(item);
                    setSearchTerm(item);
                    setShowSuggestions(false);
                  }}
                >
                  {item}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Amount Input */}
        <div className="form-group">
          <label>Amount</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            step="0.25"
            min="0"
          />
        </div>

        {/* From Unit */}
        <div className="form-group">
          <label>From Unit</label>
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
          >
            {allUnits.map(unit => (
              <option key={`from-${unit}`} value={unit}>
                {unit}
              </option>
            ))}
          </select>
        </div>

        {/* To Unit */}
        <div className="form-group">
          <label>To Unit</label>
          <select
            value={toUnit}
            onChange={(e) => setToUnit(e.target.value)}
          >
            {allUnits.map(unit => (
              <option key={`to-${unit}`} value={unit}>
                {unit}
              </option>
            ))}
          </select>
        </div>

        {/* Convert Button */}
        <motion.button
          type="submit"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="convert-btn"
          disabled={isLoading}
        >
          <Scale className="btn-icon" />
          {isLoading ? 'Converting...' : `Convert to ${toUnit}`}
        </motion.button>

        {/* Error Message */}
        {error && <div className="error-message">{error}</div>}
      </form>

      {/* Result Display */}
      {result && (
        <div className="result-container">
          <div className="result-header">
            <ChefHat className="result-icon" />
            <h3>Conversion Result</h3>
          </div>
          <div className="result-content">
            <p>{result.input} of {result.ingredient}</p>
            <p className="result-value">= {result.output}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ConversionForm;
