import React, { useState } from "react";
import Navbar from "../components/Navbar";
import ConversionForm from "../components/ConversionForm"; // ✅ Using the form
import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api"; // ✅ Correct backend URL

const ConversionPage = () => {
  const [conversionResult, setConversionResult] = useState(null);

  return (
    <div className="max-w-4xl mx-auto p-6">
      <Navbar />
      <h2 className="text-2xl font-bold text-center">Food Measurement Converter</h2>

      {/* ✅ Conversion Form */}
      <ConversionForm onConvert={setConversionResult} />

      {/* ✅ Show Results if Available */}
      {conversionResult && (
        <div className="mt-4 p-4 bg-gray-100 rounded-lg">
          <h3 className="text-lg font-semibold">Conversion Result</h3>
          <p>{conversionResult.result}</p>
        </div>
      )}
    </div>
  );
};

export default ConversionPage;
