import React, { useState } from "react";
import axios from "axios";

const SearchBar = ({ onSelectFood }) => {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  const fetchFoods = async (searchQuery) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/search_foods?query=${searchQuery}`);
      setSuggestions(response.data);
    } catch (error) {
      console.error("Error fetching food data:", error);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search food..."
        value={query}
        onChange={(e) => {
          setQuery(e.target.value);
          if (e.target.value.length > 1) fetchFoods(e.target.value);
        }}
        style={{ width: "80%", padding: "8px" }}
      />
      {suggestions.length > 0 && (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {suggestions.map((food, index) => (
            <li key={index} onClick={() => { onSelectFood(food); setQuery(food); setSuggestions([]); }}>
              {food}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
