import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { Scale, Home, Info, Search, ChefHat } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import Fuse from "fuse.js";
import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";
const units = ["cup", "tablespoon", "teaspoon", "glass"];

export default function ConversionPage() {
  const [foodList, setFoodList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentIngredient, setCurrentIngredient] = useState("");
  const [amount, setAmount] = useState("1");
  const [measurement, setMeasurement] = useState("cup");
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);

  // Fetch food list from backend
  useEffect(() => {
    axios.get(`${API_BASE_URL}/foods`)
      .then((res) => setFoodList(res.data))
      .catch((err) => console.error("Error fetching foods:", err));
  }, []);

  // Fuse.js for fuzzy search
  const fuse = new Fuse(foodList, { threshold: 0.4, distance: 100 });
  const suggestions = searchTerm
    ? fuse.search(searchTerm).map((result) => result.item)
    : [];

  // Handle food selection
  const handleIngredientSelect = (ingredient) => {
    setCurrentIngredient(ingredient);
    setSearchTerm(ingredient);
    setShowSuggestions(false);
  };

  // Handle conversion request
  const handleConvert = async (e) => {
    e.preventDefault();
    setError(null);

    if (!currentIngredient || !amount || !measurement) {
      setError("Please enter all fields.");
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/convert`, {
        food: currentIngredient,
        quantity: amount,
        unit: measurement,
      });

      setResults((prev) => [
        {
          ingredient: currentIngredient,
          amount: parseFloat(amount),
          measurement,
          grams: response.data.value,
        },
        ...prev,
      ]);
    } catch (err) {
      setError("Conversion failed. Please check input and try again.");
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      
      {/* ðŸŸ¢ Navbar */}
      <nav className="bg-white shadow-sm mb-8">
        <div className="max-w-4xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <motion.div 
              className="flex items-center gap-3"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
            >
              <Scale className="w-8 h-8 text-blue-600" />
              <h1 className="text-2xl font-bold text-gray-900">Precise Baking</h1>
            </motion.div>

            <motion.div 
              className="flex gap-6"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
            >
              <NavLink
                to="/"
                className={({ isActive }) =>
                  `flex items-center gap-2 text-${isActive ? 'blue-600' : 'gray-600'} hover:text-blue-600 transition-colors`
                }
              >
                <Home className="w-5 h-5" />
                <span>Home</span>
              </NavLink>
              <NavLink
                to="/about"
                className={({ isActive }) =>
                  `flex items-center gap-2 text-${isActive ? 'blue-600' : 'gray-600'} hover:text-blue-600 transition-colors`
                }
              >
                <Info className="w-5 h-5" />
                <span>About</span>
              </NavLink>
            </motion.div>
          </div>
        </div>
      </nav>

      {/* ðŸŸ¢ Conversion Form */}
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="bg-white rounded-xl shadow-lg p-6 mb-8">
        <form onSubmit={handleConvert} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            
            {/* Search Ingredient */}
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search Ingredient
              </label>
              <div className="relative">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setShowSuggestions(true);
                  }}
                  onFocus={() => setShowSuggestions(true)}
                  className="w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 pl-10"
                  placeholder="Type to search..."
                />
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
              </div>

              {/* Suggestions Dropdown */}
              <AnimatePresence>
                {showSuggestions && suggestions.length > 0 && (
                  <motion.div className="absolute z-10 w-full mt-1 bg-white rounded-lg shadow-lg border border-gray-200">
                    {suggestions.map((ingredient, index) => (
                      <motion.button key={ingredient} type="button" onClick={() => handleIngredientSelect(ingredient)} className="w-full text-left px-4 py-2 hover:bg-blue-50 transition-colors first:rounded-t-lg last:rounded-b-lg">
                        {ingredient}
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Amount & Measurement */}
            <div className="grid grid-cols-2 gap-4">
              <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} step="0.125" min="0" className="w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500" />
              <select value={measurement} onChange={(e) => setMeasurement(e.target.value)} className="w-full rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500">
                {units.map((u) => (
                  <option key={u} value={u}>{u}</option>
                ))}
              </select>
            </div>
          </div>

          {/* Convert Button */}
          <motion.button type="submit" whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }} className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2">
            <Scale className="w-5 h-5" />
            Convert to Grams
          </motion.button>
        </form>
      </motion.div>

      {/* ðŸŸ¢ Results Display */}
      <AnimatePresence>
        {results.length > 0 && results.map((result, index) => (
          <motion.div key={index} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: index * 0.1 }} className="p-4 bg-gray-50 rounded-lg flex items-center justify-between hover:bg-gray-100 transition-colors">
            <span>{result.amount} {result.measurement} of {result.ingredient}</span>
            <span className="text-blue-600 font-bold">{result.grams}g</span>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
}
